var httpClient = new HttpClient();
var builder = new ConfigurationBuilder()
    .SetBasePath(Directory.GetCurrentDirectory())
    .AddJsonFile("config.json", true)
    .Build();

var grantType = GrantType.Password;

//Create a dictionary for content data, dictionary keys will usually be specified by the 3rd party client
var contentData = new Dictionary
{
    {"client_id", builder["Salesforce:ClientId"]},
    {"client_secret", builder["Salesforce:ClientSecret"]},
    {"username", builder["Salesforce:UserName"]},
    {"password", builder["Salesforce:Password"] + builder["Salesforce:Token"]}
};

//Call the library method
var auth = new Authenticate(httpClient);
var authData = auth.InvokeAsync(grantType, builder["Salesforce:TokenUrl"], contentData);
var final = authData.Result;
