using System;
using System.Linq;
using System.Net.Http;
using System.Threading.Tasks;
using SalesforceConnect.Aws;
using SalesforceConnect.Enums;
using SalesforceConnect.SalesforceApiCalls;
using SalesforceConnect.Settings;

namespace SalesforceConnectTestClient
{
    public class Program
    {
        public static void Main(string[] args)
        {
            var httpClient = new HttpClient();

            //Connect to Salesforce
            var salesforceConnect = new Connect(httpClient);
            var data = salesforceConnect.TryConnect();
            Console.WriteLine($"Error : {data.Error}");
            Console.WriteLine($"Instance Url : {data.InstanceUrl}");
            Console.WriteLine($"Access Token : {data.AccessToken}");

            //Get EventLog object based on a date
            var restCalls = new RestCalls(httpClient, data);
            var eventLogData = restCalls.GetEventLogsInfo(EventType.Login,DateTime.Now.AddDays(-10));
            Task.WaitAll(eventLogData);
            var eventData = eventLogData.Result;

            //Download log files to a folder
            var downloadLocation = @"C:\Projects\SimpleSalesforceConnect\download_logs";
            var downloadTasks = restCalls.DownloadLogfiles(downloadLocation, eventData);
            Task.WaitAll(downloadTasks.ToArray());

            //Connect to Aws
            var s3Client = new AwsConnect();
            var client = s3Client.S3Client(Amazon.RegionEndpoint.USEast1);
            var uploadLogFiles = eventData.Records.Select(s => $@"{downloadLocation}\{s.Id}.log").ToList();

            //Upload files to S3
            var s3=new S3Operations(client);
            var tasks = s3.UploadFiles(uploadLogFiles, AwsSettings.ConnectionParams);
            Task.WaitAll(tasks.ToArray());

            Console.ReadLine();
        }
    }
}
